import React, { useState } from 'react';
import './Chatbot.css';
import FruitList from './FruitList';
import FruitDetail from './FruitDetail';

const fruitsData = [
  { id: 1, name: 'Apple', description: 'A sweet red fruit.', image: 'apple.jpg' },
  { id: 2, name: 'Banana', description: 'A long yellow fruit.', image: 'banana.jpg' },
  // Add more fruits here
];

const Chatbot = () => {
  const [selectedFruit, setSelectedFruit] = useState(null);

  const handleFruitSelect = (fruit) => {
    setSelectedFruit(fruit);
  };

  const handleBackToList = () => {
    setSelectedFruit(null);
  };

  return (
    <div className="chatbot">
      <div className="chatbot-messages">
        {selectedFruit ? (
          <FruitDetail fruit={selectedFruit} onBack={handleBackToList} />
        ) : (
          <FruitList fruits={fruitsData} onSelect={handleFruitSelect} />
        )}
      </div>
    </div>
  );
};

export default Chatbot;
